mapper.insert:
  		insert into attendance(
			mid, adate, acheckin, astatus, aconfirm, cno, 
			approvecnt, latenesscnt, absencecnt
		)
		values (
			#{mid}, #{adate}, #{acheckin}, #{astatus}, #{aconfirm}, #{cno},
			#{approvecnt}, #{latenesscnt}, #{absencecnt}
		)

mapper.update:
update attendance set
	acheckout=#{acheckout}
where mid=#{mid} and adate=#{adate}
