mapper.select:
select count(trname)
from trainingroom
where ecno=#{ecno} and trname=#{trname}

mapper.update:
update trainingroom set
	trname=#{room.trname}, trcapacity=#{room.trcapacity}, trenable=#{room.trenable}, trupdatedat=sysdate

		, trattach=#{room.trattach}
		, trattachoname=#{room.trattachoname}
		, trattachtype=#{room.trattachtype}

where trno=#{trno}
