mapper.select:
select count(trname)
from trainingroom
where ecno=#{ecno} and trname=#{trname}

mapper.update:
update trainingroom set
	trname=#{room.trname}, trcapacity=#{room.trcapacity}, trenable=#{room.trenable}, trupdatedat=sysdate
where trno=#{trno}

update trainingroom set
	trenable=#{trenable}, trupdatedat=sysdate
where trno=#{trno}

mapper.select:
select trno
from trainingroom
where ecno=#{ecno} and trname=#{trname}
